--修改预测计划供应商驳回待确认的待办
update QEWEB_BACKLOG_CFG 
set QUERY_SQL = 'SELECT count(DISTINCT A.id) FROM QEWEB_PURCHASE_PLAN_ITEM A LEFT JOIN QEWEB_PURCHASE_PLAN B ON A.PLAN_ID = B.ID LEFT JOIN QEWEB_ORGANIZATION C ON A .VENDOR_ID = C. ID LEFT JOIN QEWEB_MATERIAL D ON A .MATERIAL_ID = D. ID LEFT JOIN QEWEB_FACTORY E ON A .FACTORY_ID = E . ID LEFT JOIN QEWEB_USER F ON A .CREATE_USER_ID = F . ID LEFT JOIN QEWEB_USER G ON A .PUBLISH_USER_ID = G. ID LEFT JOIN QEWEB_PURCHASING_GROUP H ON A . GROUP_ID = H. ID WHERE A .ABOLISHED = 0 AND A.PUBLISH_STATUS = 1 AND A.CONFIRM_STATUS=-1 AND A .IS_NEW = '1' AND B.CREATE_USER_ID = #currentUserId#' 
where id = '6056654'


