INSERT INTO "QEWEB_BACKLOG_CFG" VALUES (6088160, '待发货提醒', 1, 6047322, 'manager/order/delivery/pending?backlogId=6088160', 1, NULL, NULL, 'SELECT ITEM_PLAN. ID FROM QEWEB_PURCHASE_ORDER_ITEM_PLAN ITEM_PLAN LEFT JOIN QEWEB_PURCHASE_ORDER_ITEM ORDER_ITEM ON ITEM_PLAN.ORDER_ITEM_ID = ORDER_ITEM. ID LEFT JOIN QEWEB_PURCHASE_ORDER PUR_ORDER ON ORDER_ITEM.ORDER_ID = PUR_ORDER. ID LEFT JOIN QEWEB_ORGANIZATION ORG ON ORG. ID = PUR_ORDER.VENDOR_ID LEFT JOIN QEWEB_MATERIAL MAT ON MAT. ID = ITEM_PLAN.MATERIAL_ID LEFT JOIN PURCHASE_GOODS_REQUEST REQ ON REQ.id=ITEM_PLAN.GOODS_REQUEST_ID WHERE ITEM_PLAN.PUBLISH_STATUS = 1 AND ITEM_PLAN.CONFIRM_STATUS = 1 AND ORDER_ITEM.CLOSE_STATUS = 0 AND ITEM_PLAN.ABOLISHED = 0 AND (ITEM_PLAN.ORDER_QTY - ITEM_PLAN.DELIVERY_QTY - TO_DELIVERY_QTY) > 0 AND PUR_ORDER.VENDOR_ID =#currentOrgId# AND ITEM_PLAN.REQUEST_TIME-to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')<=to_number(REQ.YSTS) ORDER BY ITEM_PLAN.REQUEST_TIME ASC', NULL, NULL, NULL, 0, NULL, 1, NULL, 1, '超级管理员', '超级管理员', '待发货看板');
INSERT INTO "QEWEB_BACKLOG_CFG" VALUES (6088172, '供应商待发货提醒', 1,6047323, 'manager/order/delivery/pending?backlogId=6088172', 0, NULL, NULL, 'SELECT ITEM_PLAN.ID  FROM QEWEB_PURCHASE_ORDER_ITEM_PLAN ITEM_PLAN LEFT JOIN QEWEB_PURCHASE_ORDER_ITEM ORDER_ITEM ON ITEM_PLAN.ORDER_ITEM_ID = ORDER_ITEM.ID LEFT JOIN QEWEB_PURCHASE_ORDER PUR_ORDER ON ORDER_ITEM.ORDER_ID = PUR_ORDER.ID LEFT JOIN QEWEB_ORGANIZATION ORG ON ORG.ID = PUR_ORDER.VENDOR_ID LEFT JOIN QEWEB_MATERIAL MAT ON MAT.ID = ITEM_PLAN.MATERIAL_ID LEFT JOIN PURCHASE_GOODS_REQUEST REQ ON REQ.id=ITEM_PLAN.GOODS_REQUEST_ID WHERE ITEM_PLAN.PUBLISH_STATUS=1 AND ITEM_PLAN.CONFIRM_STATUS=1 AND ORDER_ITEM.CLOSE_STATUS=0 AND ITEM_PLAN.ABOLISHED=0 AND (ITEM_PLAN.ORDER_QTY-ITEM_PLAN.DELIVERY_QTY-TO_DELIVERY_QTY)>0 AND ITEM_PLAN.REQUEST_TIME-to_date(to_char(sysdate,'yyyy-mm-dd'),'yyyy-mm-dd')<=to_number(REQ.YSTS) AND PUR_ORDER.PURCHASING_GROUP_ID IN #currentGroupId#  ORDER BY ITEM_PLAN.REQUEST_TIME ASC', NULL, NULL, NULL, 0, NULL, 1, NULL, 1, '超级管理员', '超级管理员', '待发货看板');
